mapper.select:
/* getDataGroupTotalCount */
SELECT COUNT(data_group_id) 
FROM data_group


		user_id = #{userId}


	AND data_group_id = #{dataGroupId}


	AND sharing = #{sharing}


	AND basic = #{basic}


	AND ${searchWord} = #{searchValue}


	AND ${searchWord} LIKE '%' || #{searchValue} || '%'



	AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListDataGroup */ WITH RECURSIVE data_group_tree ( data_group_id, data_group_name, data_group_key, data_group_path, data_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, tiling, data_count, location, altitude, duration, label_template, location_update_type, metainfo, description, update_date, insert_date, path, cycle ) AS ( SELECT A.data_group_id, A.data_group_name, A.data_group_key, A.data_group_path, A.data_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.tiling, A.data_count, A.location, A.altitude, A.duration, A.label_template, A.location_update_type, A.metainfo, A.description, A.update_date, A.insert_date, ARRAY[A.view_order], false FROM data_group A WHERE A.data_group_target = 'admin'
AND data_group_key != 'basic'
AND A.available = #{available}
AND A.data_group_id = #{dataGroupId}
AND A.parent = 0
UNION ALL SELECT A.data_group_id, A.data_group_name, A.data_group_key, A.data_group_path, A.data_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.tiling, A.data_count, A.location, A.altitude, A.duration, A.label_template, A.location_update_type, A.metainfo, A.description, A.update_date, A.insert_date, path || A.view_order, A.data_group_id = ANY(path) FROM data_group A, data_group_tree B WHERE A.parent = B.data_group_id AND not cycle ) SELECT data_group_id, data_group_name, data_group_key, data_group_path, data_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, tiling, data_count, location, altitude, duration, label_template, location_update_type, metainfo, description, update_date, insert_date, path FROM data_group_tree ORDER BY path

/* getDataGroup */
SELECT A.*, ST_X(A.location) AS longitude, ST_Y(A.location) AS latitude,
	CASE 
		WHEN parent > 0 THEN ( SELECT data_group_name FROM data_group WHERE data_group_id = A.parent )
		ELSE null
	END AS parentName
FROM data_group A
WHERE A.data_group_id = #{dataGroupId} 

	AND A.user_id = #{userId}

/* getUserGroupByParentAndViewOrder */
SELECT *
FROM data_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

ORDER BY view_order DESC



	AND view_order > #{viewOrder}

ORDER BY view_order

LIMIT 1

/* isDataGroupKeyDuplication */
SELECT 
	CASE 
		WHEN COUNT(*) > 0 THEN true
		ELSE false
	END AS duplication
FROM data_group
WHERE data_group_key = #{dataGroupKey}
	AND data_group_target = 'admin'

	AND user_id = #{userId}

/* getDataGroupListByAncestor */
SELECT data_group_id
FROM data_group
WHERE ancestor = #{ancestor}

	AND user_id = #{userId}

/* getDataGroupListByParent */
SELECT data_group_id
FROM data_group
WHERE parent = #{ancestor}

	AND user_id = #{userId}

mapper.update:
/* updateDataGroupViewOrder */
UPDATE data_group
SET view_order = #{viewOrder}
WHERE data_group_id = #{dataGroupId}

	AND user_id = #{userId}

/* updateDataGroupChildren */
UPDATE data_group
SET children = children + #{children}
WHERE data_group_id = #{dataGroupId}

	AND user_id = #{userId}

mapper.delete:
/* deleteDataGroupByAncestor */
DELETE
FROM data_group
WHERE ancestor = #{dataGroupId}
	OR data_group_id = #{dataGroupId}

	AND user_id = #{userId}

/* deleteDataGroupByParent */
DELETE
FROM data_group
WHERE parent = #{dataGroupId}
	OR data_group_id = #{dataGroupId}

	AND user_id = #{userId}

/* deleteDataGroup */
DELETE
FROM data_group
WHERE data_group_id = #{dataGroupId}

	AND user_id = #{userId}

/* deleteDataGroup */
DELETE
FROM data_group
WHERE user_id = #{value}
