mapper.select:
/* getListMenu */ WITH RECURSIVE menu_tree ( menu_id, menu_type, menu_target, name, name_en, ancestor, parent, depth, previous_depth, view_order, url, url_alias, alias_menu_id, html_id, html_content_id, image, image_alt, css_class, default_yn, use_yn, display_yn, description, insert_date, path, cycle ) AS ( SELECT A.menu_id, A.menu_type, A.menu_target, A.name, A.name_en, A.ancestor, A.parent, A.depth, A.previous_depth, A.view_order, A.url, A.url_alias, A.alias_menu_id, A.html_id, A.html_content_id, A.image, A.image_alt, A.css_class, A.default_yn, A.use_yn, A.display_yn, A.description, A.insert_date, ARRAY[A.view_order], false FROM menu A WHERE A.menu_target = #{menuTarget}
AND A.menu_type = #{menuType}
AND A.parent = 0
AND default_yn = #{defaultYn}
UNION ALL SELECT A.menu_id, A.menu_type, A.menu_target, A.name, A.name_en, A.ancestor, A.parent, A.depth, A.previous_depth, A.view_order, A.url, A.url_alias, A.alias_menu_id, A.html_id, A.html_content_id, A.image, A.image_alt, A.css_class, A.default_yn, A.use_yn, A.display_yn, A.description, A.insert_date, path || A.view_order, A.menu_id = ANY(path) FROM menu A, menu_tree B WHERE A.menu_target = #{menuTarget}
AND A.menu_type = #{menuType}
AND A.parent = B.menu_id AND not cycle ) SELECT menu_id, menu_type, menu_target, name, name_en, ancestor, parent, depth, previous_depth, view_order, url, url_alias, alias_menu_id, html_id, html_content_id, image, image_alt, css_class, default_yn, use_yn, display_yn, description, insert_date, path FROM menu_tree ORDER BY path

/* getMenuByParentAndViewOrder */
SELECT * 
FROM menu 
WHERE menu_target = #{menuTarget}
	AND parent = #{parent} 


	AND view_order >= #{viewOrder} - 1
	AND view_order < #{viewOrder}




	AND view_order > #{viewOrder}
	AND view_order <= #{viewOrder} + 1


ORDER BY view_order
LIMIT 1

/* getMaxViewOrderChildMenu */
SELECT * 
FROM menu 
WHERE menu_target = #{menuTarget}
	AND parent = #{parent} 
ORDER BY view_order DESC 
LIMIT 1

mapper.insert:
		/* insertMenu */

            SELECT NEXTVAL('menu_seq')

		INSERT INTO menu(
			menu_id, menu_type, menu_target, name, name_en, ancestor, parent, depth, view_order, url, url_alias, alias_menu_id,
			html_id, html_content_id, image, image_alt, css_class, default_yn, use_yn, display_yn, description
		) VALUES (
			#{menuId}, #{menuType}, #{menuTarget}, #{name}, #{nameEn}, 
			(
				SELECT COALESCE(MAX(ancestor), #{menuId})
				FROM menu
				WHERE menu_id = #{parent}
					AND menu_id != 0
			),
			#{parent}, #{depth}, #{viewOrder}, #{url}, #{urlAlias}, #{aliasMenuId},
			#{htmlId}, #{htmlContentId}, #{image}, #{imageAlt}, #{cssClass}, #{defaultYn}, #{useYn}, #{displayYn}, #{description} 
		)
