mapper.select:
/* getDataGroupByParentAndViewOrder */
SELECT * 
FROM data_group 
WHERE parent = #{parent} 


	AND view_order < #{view_order}

ORDER BY view_order DESC 



	AND view_order > #{view_order}

ORDER BY view_order 

LIMIT 1

mapper.insert:
/* insertDataGroup */
INSERT INTO data_group(
	data_group_id, data_group_key, data_group_name, ancestor, parent, depth, view_order, use_yn, description
) values(
	#{data_group_id}, #{data_group_key}, #{data_group_name}, #{ancestor}, #{parent}, #{depth}, #{view_order}, #{use_yn}, #{description}
)

mapper.update:
/* updateDataGroup */ UPDATE data_group SET data_group_key = #{data_group_key}, data_group_name = #{data_group_name},
ancestor = #{ancestor},
parent = #{parent},
depth = #{depth},
view_order = #{view_order},
use_yn = #{use_yn}, description = #{description} WHERE data_group_id = #{data_group_id}

UPDATE data_group
SET child_yn = #{child_yn}
WHERE data_group_id = #{data_group_id}
