mapper.select:
	/* getListProject */
	SELECT * 
	FROM project

    WHERE use_yn = #{use_yn}

	ORDER BY view_order

/* getDuplicationKeyCount */
SELECT COUNT(project_key) AS count 
FROM project
WHERE project_key = #{project_key}

mapper.insert:
		/* insertProject */

    		SELECT NEXTVAL('project_seq')

		INSERT INTO project(
			project_id, project_key, project_name, view_order, use_yn, latitude, longitude, height, duration, description
		) values(
			#{project_id}, #{project_key}, #{project_name}, #{view_order}, #{use_yn}, #{latitude}, #{longitude}, #{height}, #{duration}, #{description}
		)

mapper.update:
/* updateProject */ UPDATE project SET project_key = #{project_key}, project_name = #{project_name},
view_order = #{view_order},
use_yn = #{use_yn}, latitude = #{latitude}, longitude = #{longitude}, height = #{height}, duration = #{duration}, description = #{description} WHERE project_id = #{project_id}
