mapper.select:
/* getUserSession */ SELECT A.*, B.user_group_name AS userGroupName,
CASE WHEN A.last_password_change_date < (NOW() - INTERVAL '${passwordChangeTerm} DAY') THEN true ELSE false END AS password_change_term_over,
CASE WHEN A.last_signin_date IS NULL THEN A.insert_date < (NOW() - INTERVAL '${userLastSigninLock} DAY') ELSE A.last_signin_date < (NOW() - INTERVAL '${userLastSigninLock} DAY') END AS user_last_signin_lock_over
FROM user_info A LEFT OUTER JOIN user_group B ON A.user_group_id = B.user_group_id WHERE A.user_id = #{userId}

mapper.update:
/* updateSigninUserSession */ UPDATE user_info SET
status = #{status}, update_date = NOW(),
fail_signin_count = #{failSigninCount},
last_signin_date = NOW() WHERE user_id = #{userId}
