mapper.select:
/* getUser */
SELECT A.*, B.user_group_name 
FROM user_info A, user_group B
WHERE A.user_id = #{userId}
	AND A.user_group_id = B.user_group_id

mapper.insert:
/* insertUser */
INSERT INTO user_info(
	user_id, user_group_id, user_name, password, department, position, email, status, update_date
) values(
	#{userId}, #{userGroupId}, #{userName}, #{password}, #{department}, #{position}, #{email}, COALESCE(#{status}, '0'), NOW()
)

mapper.update:
/* updatePassword */
UPDATE user_info
SET password = #{password},
	update_date = NOW(),
	last_password_change_date = NOW(),

	fail_login_count = 0,


	last_login_date = NOW(),

	status = #{status}
WHERE user_id = #{userId}
